import React, {useState, useEffect, useLocation} from "react";
import { slide as Menu } from 'react-burger-menu';
import logo from "../../../assets/img/LogoPV2.png";
import "./Nav.css";

export default function Nav(props) {


  // const [isMenuOpen, setIsMenuOpen] = React.useState(false);

  // const handleCloseMenu = () => {
  //   setIsMenuOpen(false);
  // };
  
  // const handleStateChange = (state) => {
  //   setIsMenuOpen(state.isOpen);
  // };

  const [navigationKey, setNavigationKey] = useState('');
    
  const location = useLocation();

  useEffect(() => {
      setNavigationKey(location);
  }, [location]);
  

  
  return (
    <nav className="nav">
      <div className="navWrapper">
        <div className="logoWrapper">
          <a href="#home" className="nav__list__link">
                  <img src={logo} alt="logo Karolina" className="nav__list__link--img" />
          </a>
        </div>
        <div className="linkWrapper">
          <ul className="nav__list">
            {/* <li className="nav__list__item">
              <a href="#home" className="nav__list__link">
                <img src={logo} alt="logo Karolina" className="nav__list__link--img" />
              </a>
            </li> */}
            <li className="nav__list__item">
              <a href="#home" className="nav__list__link">
                HOME
              </a>
            </li>
            <li className="nav__list__item">
              <a href="#portfolioSection" className="nav__list__link">
                MY WORK
              </a>
            </li>
            <li className="nav__list__item">
              <a href="#aboutSection" className="nav__list__link">
                ABOUT
              </a>
            </li>
            <li className="nav__list__item">
              <a href="#contactSection" className="nav__list__link">
                CONTACT
              </a>
            </li>
          </ul>
        </div>
      </div>

      <div className="navBurgerContainer">
        <Menu key={navigationKey}>
          <button ><a id="home" className="menu-item" href="#home">Home</a></button>
          <a  id="about" className="menu-item" href="#portfolioSection">Portfolio</a>
          <a  id="contact" className="menu-item" href="#aboutSection">About Me</a>
          <a  id="contact" className="menu-item" href="#contactSection">Contact</a>
        </Menu>
      </div>
      
    </nav>

  );
}
