import React, {useState, useEffect} from "react";
import { slide as Menu } from 'react-burger-menu';
import logo from "../../../assets/img/LogoPV2.png";
import "./Nav.css";

export default function Nav() {

  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => setIsOpen(!isOpen);
  const hide = () => setIsOpen(false);
  const show = () => setIsOpen(true);

   //Close nav when escape key is pressed
   useEffect(() => {
    const closeMenu = (event) => {
      if (event.key === 'Escape') {
        setIsOpen(false);
      }
    };
    window.addEventListener('keydown', closeMenu);
    return () => {
      window.removeEventListener('keydown', closeMenu);
    };
  }, []);

  const navigation = [
    { link: '#home', text: 'Home' },
    { link: '#portfolioSection', text: 'Work' },
    { link: '#aboutSection', text: 'About' },
    { link: '#contactSection', text: 'Contact' },
  ];

  
  return (
    <nav className="nav">
      <div className="navWrapper">
        <div className="logoWrapper">
          <a href="#home" className="nav__list__link">
                  <img src={logo} alt="logo Karolina" className="nav__list__link--img" />
          </a>
        </div>
        <div className="linkWrapper">
          <ul className="nav__list">
            {/* <li className="nav__list__item">
              <a href="#home" className="nav__list__link">
                <img src={logo} alt="logo Karolina" className="nav__list__link--img" />
              </a>
            </li> */}
            <li className="nav__list__item">
              <a href="#home" className="nav__list__link">
                HOME
              </a>
            </li>
            <li className="nav__list__item">
              <a href="#portfolioSection" className="nav__list__link">
                MY WORK
              </a>
            </li>
            <li className="nav__list__item">
              <a href="#aboutSection" className="nav__list__link">
                ABOUT
              </a>
            </li>
            <li className="nav__list__item">
              <a href="#contactSection" className="nav__list__link">
                CONTACT
              </a>
            </li>
          </ul>
        </div>
      </div>

    <div className="">
      <button className="menu-toggle" onClick={toggle}>
          <span className={`menu ${isOpen ? 'cross' : 'hamburger'}`}></span>
        </button>
        <ul className="menu-links">
          {navigation.map(nav => (
            <li key={nav.text}>
              <a href={nav.link} onClick={toggle} onBlur={hide} onFocus={show}>
                {nav.text}
              </a>
            </li>
          ))}
        </ul>
    </div>
      
    </nav>

  );
}
